mapper.select:
SELECT


,shangjia.shangjia_name as shangjiaName
,shangjia.shangjia_phone as shangjiaPhone
,shangjia.shangjia_email as shangjiaEmail
,shangjia.shangjia_photo as shangjiaPhoto
,shangjia.shangjia_gongzuoshijian as shangjiaGongzuoshijian
,shangjia.shangjia_peisongfanwei as shangjiaPeisongfanwei
,shangjia.shangjia_zhuying as shangjiaZhuying
,shangjia.shangjia_adress as shangjiaAdress
,shangjia.shangjia_xingji_types as shangjiaXingjiTypes
,shangjia.new_money as newMoney
,shangjia.shangjia_content as shangjiaContent
,shangjia.shangjia_delete as shangjiaDelete

FROM caipin  a
left JOIN shangjia shangjia ON a.shangjia_id = shangjia.id



        and a.id in

        #{item}



        and (
            a.shangjia_id = #{params.shangjiaId}
        )


        and a.caipin_name like CONCAT('%',#{params.caipinName},'%')


        and a.caipin_uuid_number like CONCAT('%',#{params.caipinUuidNumber},'%')


        and a.caipin_types = #{params.caipinTypes}


          and a.caipin_kucun_number >= #{params.caipinKucunNumberStart}   


          and a.caipin_kucun_number <= #{params.caipinKucunNumberEnd}   


        and a.caipin_kucun_number = #{params.caipinKucunNumber}


          and a.caipin_old_money >= #{params.caipinOldMoneyStart}   


          and a.caipin_old_money <= #{params.caipinOldMoneyEnd}   


          and a.caipin_new_money >= #{params.caipinNewMoneyStart}   


          and a.caipin_new_money <= #{params.caipinNewMoneyEnd}   


          and a.caipin_clicknum >= #{params.caipinClicknumStart}   


          and a.caipin_clicknum <= #{params.caipinClicknumEnd}   


        and a.caipin_clicknum = #{params.caipinClicknum}


        and a.caipin_content like CONCAT('%',#{params.caipinContent},'%')


        and a.shangxia_types = #{params.shangxiaTypes}


          and a.caipin_delete >= #{params.caipinDeleteStart}   


          and a.caipin_delete <= #{params.caipinDeleteEnd}   


        and a.caipin_delete = #{params.caipinDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.shangjia_id IS NOT NULL


        and shangjia.shangjia_name like CONCAT('%',#{params.shangjiaName},'%')


        and shangjia.shangjia_phone like CONCAT('%',#{params.shangjiaPhone},'%')


        and shangjia.shangjia_email like CONCAT('%',#{params.shangjiaEmail},'%')


        and shangjia.shangjia_gongzuoshijian like CONCAT('%',#{params.shangjiaGongzuoshijian},'%')


        and shangjia.shangjia_peisongfanwei like CONCAT('%',#{params.shangjiaPeisongfanwei},'%')


        and shangjia.shangjia_zhuying like CONCAT('%',#{params.shangjiaZhuying},'%')


        and shangjia.shangjia_adress like CONCAT('%',#{params.shangjiaAdress},'%')


        and shangjia.shangjia_xingji_types = #{params.shangjiaXingjiTypes}



          and shangjia.new_money >= #{params.newMoneyStart}   


          and shangjia.new_money <= #{params.newMoneyEnd}   


        and shangjia.shangjia_content like CONCAT('%',#{params.shangjiaContent},'%')


          and shangjia.shangjia_delete >= #{params.shangjiaDeleteStart}   


          and shangjia.shangjia_delete <= #{params.shangjiaDeleteEnd}   


        and shangjia.shangjia_delete = #{params.shangjiaDelete}


order by a.${params.sort} ${params.order}
